import React from 'react';
import ImpactIndicator from '../ImpactIndicator/ImpactIndicator';

const getImpactWeight = (impact) => {
  const weights = {
    'critical': 3,
    'serious': 2,
    'moderate': 1,
    'minor': 0, // Ajoutez ceci si vous avez également des impacts mineurs
  };
  return weights[impact] || 0;
};

const ErrorList = ({ errors, onSelectError }) => {
  // Assurez-vous de trier une copie des erreurs pour éviter de modifier l'état original
  const sortedErrors = [...errors].sort((a, b) => getImpactWeight(b.impact) - getImpactWeight(a.impact));

  return (
    <ul className="space-y-2">
      {sortedErrors && sortedErrors.map((error, index) => (
        <li key={index} onClick={() => onSelectError(error)} className="cursor-pointer hover:bg-gray-200 py-2 px-4 flex justify-between items-center">
          <div className='max-w-xs truncate'> {/* Utilisez truncate pour couper le texte si c'est trop long */}
            {error.help}
          </div>
          <ImpactIndicator impact={error.impact} />
        </li>
      ))}
    </ul>
  );
};

export default ErrorList;
