import React from 'react';
import ImpactIndicator from '../ImpactIndicator/ImpactIndicator';

const ErrorList = ({ errors, onSelectError }) => {
    return (
        <ul>
            {errors && errors.map((error, index) => (
                <li key={index} onClick={() => onSelectError(error)} className="cursor-pointer hover:bg-gray-200 py-2 px-2 flex justify-between items-center">
                    {error.help} {/* Le titre de l'erreur */}
                    <ImpactIndicator impact={error.impact} />
                </li>
            ))}
        </ul>
    );
};

export default ErrorList;
