import React, { useState } from 'react';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import Input from '../components/Input/Input';
import Button from '../components/Buttons/Button';

function Accebility() {
  const [url, setUrl] = useState('');
  const [markdown, setMarkdown] = useState('');

  const audit = async () => {
    try {
        const response = await axios.post('http://localhost:3000/audit', { url });
        setMarkdown(response.data);
    } catch (error) {
        console.error('Erreur lors de l\'audit', error);
        alert('Erreur lors de l\'audit');
    }
};

    return (
      <div className='flex flex-col justify-center items-center gap-4 mt-10'>
        <h1>Audit d'Accessibilit√©</h1>
        <div className='flex flex-row gap-1'>
            <div className='w-80'>
                <Input 
                type="text"
                value={url}
                onChangeFunction={(e) => setUrl(e.target.value)}
                placeholder="Entrez l'URL ici"

            />
            </div>
            <Button onClickFunction={audit} ContentText="Commencer l'audit" />
         </div>
        <div>
          <ReactMarkdown>{markdown}</ReactMarkdown>
      </div>
  </div>
    );
}

export default Accebility;
