import React from 'react';

const ErrorDetails = ({ error }) => {
    if (!error) {
        return <div>Sélectionnez une erreur pour voir les détails.</div>;
    }

    return (
        <div>
            <h1 className='text-xl font-bold text-gray-900'>Détails de l'Erreur</h1>
            <p><strong>Description:</strong> {error.description}</p>
            <p><strong>Impact:</strong> {error.impact}</p>
            <p><strong>Aide:</strong> <a href={error.helpUrl} target="_blank" rel="noopener noreferrer">{error.help}</a></p>
            <p><strong>Tags:</strong> {error.tags.join(', ')}</p>
            <div>
                <strong>Sélecteurs Affectés:</strong>
                <ul>
                    {error.nodes.map((node, index) => (
                        <li key={index}>
                            <code>{node.target.join(', ')}</code>
                            <div>
                                <strong>HTML:</strong> <code>{node.html}</code>
                            </div>
                            {node.any.length > 0 && (
                                <div>
                                    <strong>Problèmes à vérifier:</strong>
                                    <ul>
                                        {node.any.map((detail, idx) => (
                                            <li key={idx}>{detail.message}</li>
                                        ))}
                                    </ul>
                                </div>
                            )}
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default ErrorDetails;
