import { BellIcon } from '@heroicons/react/24/outline'
import React, { useState, useEffect } from 'react';
import ErrorList from '../ErrorComposent/ErrorList'
import ErrorDetails from '../ErrorComposent/ErrorDetails';
import Input from '../Input/Input';
import Button from '../Buttons/Button'
import axios from 'axios';

export default function Example() {
  const [url, setUrl] = useState('');
  const [results, setResults] = useState(null);
  const [selectedError, setSelectedError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
        try {
            const response = await axios.get('/api/audit-results'); // Remplacer par l'URL de votre API
            setResults(response.data);
        } catch (error) {
            console.error('Erreur lors de la récupération des données', error);
            // Gérer l'erreur comme il convient
        }
    };

    fetchData();
}, []);

const audit = async () => {
  try {
      const response = await axios.post('http://localhost:3000/audit', { url });
      setResults(response.data);
  } catch (error) {
      console.error('Erreur lors de l\'audit', error);
      alert('Erreur lors de l\'audit');
  }
};

  return (
    <div className="flex min-h-full flex-col">
      <header className="shrink-0 border-b border-gray-200 bg-white">
        <div className="mx-auto flex h-16 max-w-7xl items-center justify-between px-4 sm:px-6 lg:px-8">
          <img
            className="h-8 w-auto"
            src="https://tailwindui.com/img/logos/mark.svg?color=indigo&shade=600"
            alt="Your Company"
          />
          <div className="flex items-center gap-x-8">
            <button type="button" className="-m-2.5 p-2.5 text-gray-400 hover:text-gray-300">
              <span className="sr-only">View notifications</span>
              <BellIcon className="h-6 w-6" aria-hidden="true" />
            </button>
            <div className='flex flex-row gap-1'>
                <div className='w-80'>
                    <Input 
                        type="text"
                        value={url}
                        onChange={(e) => setUrl(e.target.value)}
                        placeholder="Entrez l'URL ici"
                    />
                </div>
                <Button onClickFunction={audit} ContentText="Auditer" />
            </div>
          </div>
        </div>
      </header>

      <div className="mx-auto flex w-full max-w-7xl items-start gap-x-8 px-4 py-10 sm:px-6 lg:px-8">
        <aside className="sticky top-8 hidden w-44 shrink-0 lg:block">
          {/* Left column area */}
          <ErrorList errors={results?.violations} onSelectError={setSelectedError} />
          </aside>

        <main className="flex-1">
          {/* Main area */}
          <ErrorDetails error={selectedError} />
          </main>

        <aside className="sticky top-8 hidden w-96 shrink-0 xl:block">{/* Right column area */}</aside>
      </div>
    </div>
  )
}
