import React, { useState } from 'react';
import axios from 'axios';

function Accebility() {
    const [url, setUrl] = useState('');
    const [results, setResults] = useState(null);

    const audit = async () => {
        try {
            const response = await axios.post('http://localhost:3000/audit', { url });
            setResults(response.data);
        } catch (error) {
            console.error('Erreur lors de l\'audit', error);
            alert('Erreur lors de l\'audit');
        }
    };

    return (
        <div>
            <h1>Audit d'Accessibilit√©</h1>
            <input
                type="text"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                placeholder="Entrez l'URL ici"
            />
            <button onClick={audit}>Auditer</button>
            <div>
                <pre>{JSON.stringify(results, null, 2)}</pre>
            </div>
        </div>
    );
}

export default Accebility;
