import React from 'react'

function ImpactIndicator({ impact }) {
  let color;
  switch (impact) {
    case 'critical':
      color = 'red';
      break;
    case 'serious':
      color = 'orange';
      break;
    case 'moderate':
      color = 'Amber-400';
      break;
    default:
      color = 'transparent';
  }
  return (
    <span
    className="inline-block mr-2 rounded-full"
    style={{ width: '10px', height: '10px', backgroundColor: color }}
    aria-label={`Impact ${impact}`}
  />
  )
}

export default ImpactIndicator
